sys_script_include.script:
var Utilities = Class.create();
Utilities.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    fiscalFunding: function() {
		var fiscalYear = gs.getProperty('x_402400_dateselec.fiscal_year');
        var fiscalYearNames = [];
        var records = new GlideRecord('x_402400_dateselec_fiscal_calendar');
		records.addEncodedQuery('u_nameLIKE' + fiscalYear);
        records.query();
        while (records.next()) {
            var name = records.u_name.toString();
            fiscalYearNames.push(name);
        }
        return 'u_nameIN' + fiscalYearNames;
    },
    type: 'Utilities'
});
